static void bin2num(char *d, char *s, int maxlen) {
  unsigned long bits;
  char *p=NULL;

  if( strlen(s) < 2 ){
    if( d != s ){
      strncpy(d, s, maxlen-1);
    }
  } else {
    if( (*s=='0') && (*(s+1)=='b') ){
      bits = strtoul(s+2, &p, 2);
      if( *p != '\0' ){
	_regerror("invalid binary value");
      }
      snprintf(d, maxlen, "%lx", bits);
      if( (strlen(d) <2) || (*d!='0') || ((*(d+1)!='x')&&((*(d+1)!='X'))) ){
        snprintf(d, maxlen, "0x%lx", bits);
      }
    } else {
      if( d != s ){
	strncpy(d, s, maxlen-1);
      }
    }
  }
}

