/*
 *
 * __regCat -- add a string to the filter string buffer
 *
 */
static void __regCat(char *str, char **ostr, int *olen) {
  int blen;
  int slen;
  
  if( (str == NULL) || (*str == '\0') ){
    return;
  } else {
    slen = strlen(str) + 1;
  }
  if( (*ostr == NULL) || (**ostr == '\0') ){
    blen = 0;
  } else {
    blen = strlen(*ostr);
  }
  while( (blen + slen) >= *olen ){
    *olen += SEGINC;
  }
  if( blen == 0 ){
    *ostr = (char *)xcalloc(*olen, sizeof(char));
  } else {
    *ostr = (char *)xrealloc(*ostr, *olen);
  }
  strcat(*ostr, str);
}

